const mongoose = require('mongoose');
const { Router } = require('express');

const pageLinkSchema = new mongoose.Schema({
  herf: {
    type: String,
  },
  text: {
    type: String,
  },
});

const PageLinkModel = mongoose.model('PageLink', pageLinkSchema);

const getPageLinks = async (req, res) => {
  const tasks = await PageLinkModel.find();
  res.send(tasks);
};

const router = Router();

router.get('/get', getPageLinks);
module.exports = router;